mapper.select:
SELECT id, boardTitle, boardWriter, boardhits, DATE_FORMAT(createdAt, "%Y-%m-%d") AS createdAt
FROM board_table
ORDER BY id DESC;

mapper.update:
UPDATE board_table
SET boardHits=boardHits + 1
WHERE id = #{id}

UPDATE board_table
SET boardTitle=#{boardTitle},
    boardContents=#{boardContents}
WHERE id = #{id};
